/**
 * Write a function to sort a list of elements using pancake sort.
 * > pancakeSort(vector<int>{15, 79, 25, 38, 69})
 * {15, 25, 38, 69, 79}
 * > pancakeSort(vector<int>{98, 12, 54, 36, 85})
 * {12, 36, 54, 85, 98}
 * > pancakeSort(vector<int>{41, 42, 32, 12, 23})
 * {12, 23, 32, 41, 42}
 */
vector<int> pancakeSort(vector<int> nums) {
  int i, j;
  for (i = 0; i < nums.size() - 1; ++i) {
    for (j = i + 1; j < nums.size(); ++j) {
      if (nums[i] > nums[j]) {
        int temp = nums[i];
        nums[i] = nums[j];
        nums[j] = temp;
      }
    }
  }
  return nums;
}

