 * > setLeftMostUnsetBit(10)
 * 14
 * > setLeftMostUnsetBit(12)
 * 14
 * > setLeftMostUnsetBit(15)
 * 15
 */
int setLeftMostUnsetBit(int n) {
    // Clearing all bits
    int i;
    for (i = n; i > 0; i--) {
        // Set i to the least unset bit
        int j;
        for (j = n; j > i; j--) {
            if (n & (1 << j)) {
                n |= 1 << i;
                break;
            }
        }
    }
    return n;
}

