/**
 * Write a function to sort the given array by using shell sort.
 * > shellSort(vector<int>{12, 23, 4, 5, 3, 2, 12, 81, 56, 95})
 * {2, 3, 4, 5, 12, 12, 23, 56, 81, 95}
 * > shellSort(vector<int>{24, 22, 39, 34, 87, 73, 68})
 * {22, 24, 34, 39, 68, 73, 87}
 * > shellSort(vector<int>{32, 30, 16, 96, 82, 83, 74})
 * {16, 30, 32, 74, 82, 83, 96}
 */
vector<int> shellSort(vector<int> myList) {
    // Write your code here
    int n = myList.size();
    int temp;
    for (int gap = n / 2; gap > 0; gap /= 2) {
        for (int i = gap; i < n; i++) {
            int j = i;
            while (j >= gap && myList[j - gap] > myList[j]) {
                temp = myList[j];
                myList[j] = myList[j - gap];
                myList[j - gap] = temp;
                j -= gap;
            }
        }
    }
    return myList;
}

