 * 4
 */
int noOfSubsequences(vector<int> arr, int k) {
    int n = arr.size();
    vector<vector<int>> dp(k + 1, vector<int> (n + 1, 0));
    for (int i = 1; i <= n; i++) {
        dp[0][i] = 1;
